import React, {FormEvent, useContext, useState} from "react";
import {User} from "../../model/IUser";
import {Location} from "../../model/ILocation";
import {LocationDetailsListItem} from "../../model/ILocationDetailsListItem";
import {LocationMainContext} from "./LocationMainProvider";
import {getListItemFromMainPage} from "../MainPage";
import {loggedUser} from "../../utils/utilsMethods";

export interface SaveLocationMainModalProps {
  switchMode: () => void;
  location: Location;
  items: LocationDetailsListItem[];
  setItems: (items: LocationDetailsListItem[]) => void;
  locations: Location[];
  setLocations: (items: Location[]) => void;
}

interface SaveLocationMainModalState {
  id?: number;
  city: string;
  country: string;
  tip: string;
  rating: number;
  user: User;
}

const AddLocationModal: React.FC<SaveLocationMainModalProps> = ({
  switchMode,
  location,
  items,
  setItems,
  locations,
  setLocations,
}) => {
  const { saving, savingError, saveLocation } = useContext(LocationMainContext);
  const initialState = {
    city: location.city,
    country: location.country,
    tip: location.tip,
    rating: location.rating,
    user: loggedUser
  };
  const [state] = useState<SaveLocationMainModalState>(initialState);


  const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    saveLocation?.(state).then((loc: Location) => {
      console.log(loc);
      if (loc) {
        const index = items.findIndex((it) => it.id === location.id);
        if (index !== -1) {
          const newItems: LocationDetailsListItem[] = items;
          newItems.splice(items.length, 0, getListItemFromMainPage(loc));
          setItems(newItems);
          switchMode();
        }
      }
    });
  };

  return (
    <div className="modal is-active">
      <div
        className="modal-background"
        onClick={() => {
          switchMode();
        }}
      />
      <div className="modal-card">
        <section className="modal-card-body has-background-light p-6">
          <div className="content">
            <p className="modal-card-title">Add Location</p>
            <form onSubmit={handleSubmit} className="form">
              <div className="field mt-4">
                <div className="label has-text-weight-light">City</div>
                <input
                  value={state.city}
                  type="text"
                  className="input"
                  readOnly
                />
              </div>
              <div className="field mt-4">
                <div className="label has-text-weight-light">Country</div>
                <input
                    value={state.country}
                    type="text"
                    className="input"
                    readOnly
                />
              </div>
              <div className="field mt-4">
                <div className="label has-text-weight-light">Tip</div>
                <input
                    value={state.tip}
                    type="text"
                    className="input"
                    readOnly
                />
              </div>
              <div className="field mt-4">
                <div className="label has-text-weight-light">Rating</div>
                <input
                    value={state.rating}
                    type="text"
                    className="input"
                    readOnly
                />
              </div>
              <div className="field mt-4">
                <div className="label has-text-weight-light">User</div>
                <input
                    value={`${state.user.firstName} ${state.user.lastName}`}
                    type="text"
                    className="input"
                    readOnly
                />
              </div>
              <div className="py-2">
                {savingError && (
                  <div className="info has-text-error is-size-7 mt-2">
                    {savingError.message} <br />
                    Please retry
                  </div>
                )}
              </div>
              {!saving && (
                <button className="button is-dark is-fullwidth">Add</button>
              )}
              {saving && (
                <div className="is-dark is-fullwidth mt-6">
                  <div className="columns is-fullwidth is-centered">
                    <div className="column is-one-third" />
                    <div className="column is-one-fifth mt-2">
                      <div className="control is-loading"></div>
                    </div>
                    <div className="column is-one-third" />
                  </div>
                </div>
              )}
              <button
                className="button is-fullwidth mt-5"
                onClick={() => {
                  switchMode();
                }}
              >
                Cancel
              </button>
            </form>
          </div>
        </section>
      </div>
    </div>
  );
};

export default AddLocationModal;
